vec3 wavePalette( float t ) {
    vec3 a = vec3(0.3, 0.4, 0.5);
    vec3 b = vec3(0.6, 0.5, 0.4);
    vec3 c = vec3(0.2, 0.7, 0.8);
    vec3 d = vec3(0.7, 0.3, 0.5);

    return a + b*sin(6.28318*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 wave = vec2(sin(uv.y * 10.0 + iTime), cos(uv.x * 10.0 + iTime));
    float intensity = 0.5 + 0.5 * sin(length(wave) * 5.0 - iTime);

    vec3 color = wavePalette(intensity + iTime);
    fragColor = vec4(color, 1.0);
}
