// variant of https://shadertoy.com/view/4XBfRy
// variant of https://shadertoy.com/view/43BfRy
// variant of https://shadertoy.com/view/4XBBRy
// variant of https://shadertoy.com/view/XXSBRy

#define rot(a) mat2(cos(a+vec4(0,33,11,0)))                       // rotation
#define C(a)   max( a.x, max(a.y,a.z)) -1.                        // cube

void mainImage( out vec4 O, vec2 u ) {

    vec3 R = iResolution,
         P = 7./R, q, a,a2,                                       // P: camera pos, then sample along ray
         D = normalize(vec3( u , -R.y ) - .5*R ),                 // D: ray direction
         M = iMouse.z > 0. ? iMouse.xyz/R -.5 :                   // comment for no mouse cam
                 vec3( .15,.07,0) * cos(.3*iTime + vec3(0,11,0));          
    float t=1., i, d,d2, l;

    for ( O=vec4(1.1); O.x > 0. && t > .01; O-=.01 )              // --- march scene
        q = P, 
        q.yz *= rot(-6.*M.y),                                     // rotations
        q.xz *= rot(-6.*M.x-1.57),
        a = abs(q), a2 = abs(q-1.),
        d = C(a), d2 = C(a2), d2 < d ? d=d2, a=a2 : a,            // union of 2 cubes
        l = min( length(a.yz), min( length(a.xy), length(a.xz) )),// 2D dist to face center
        t = max( abs(d+.05)-.05, l < 1. ? .015 -.03*sin(4.5*6.28*l) : l-1.),
        P += .3*t*D;                                              // step along the ray

 // O *= O*O;                                                     // color scheme
}
