float flag(vec2 uv, float begin, float end)
{
    //-1<=begin<=end<=1
    return step(begin,uv.x) - step(end,uv.x) ;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    p *= 1.4;
    m *= 1.4;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+p.xyx+vec3(0,2,4));

    //
    vec3 color_1 = vec3(1.0, 0.0, 0.0);
    col = mix(col, color_1, flag(p,0.0,0.1));
    vec3 color_2 = vec3(1.0, 1.0, 0.0);
    col = mix(col, color_2, flag(p,0.1,0.2));
    vec3 color_3 = vec3(0.0, 0.0, 1.0);
    col = mix(col, color_3, flag(p,0.2,0.3));


    // Output to screen
    fragColor = vec4(col,1.0);
}
