const float vRotation  =   .5;
const float vZoom      =   .05;
const float scaleZoom  = 10. ;
const float complexity = 25. ;


const vec3 a = vec3(  .698,   .658,   .218); // bgcolor
const vec3 b = vec3(  .051,   .453,   .050); 

const vec3 c = vec3(-0.552,  5.361, -0.764);
const vec3 d = vec3(-0.503, -0.503,  8.113);


vec3 palette( float t ) {
    return a + b*cos( 1.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {



    vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;

    float zoom = 1.0 + scaleZoom*sin(vZoom*iTime);
    uv /= zoom;

    float cameraRotation = vRotation*iTime;
    float cs = cos(cameraRotation);
    float sn = sin(cameraRotation);
    uv *= mat2(cs, -sn, sn, cs);

    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);
    for (float i=0.; i<complexity*float(int(iTime)%100)/100.; i++) {

        float angle = 7.0 * 3.14159 * (i / 7.);
        float s = sin(angle);
        float c = cos(angle);

        mat2 m = mat2(c, -s * 1.5, .5*s, c);
        uv *= m;
        uv = fract(1.5*uv) - .5;

        float d = length(uv) * exp(-length(uv0));

        d = sin(d*8. + iTime)/8.;
        d = abs(d);
        d = pow(0.01 / d, 2.2);

        vec3 col = palette(length(uv0) + .1*i + .2*iTime);
        finalColor += col*d;
    }  

    fragColor = vec4(finalColor, 1.0);
}
