void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Определяем размеры экрана
    vec2 resolution = iResolution.xy;

    // Определяем центр экрана
    vec2 center = resolution / 2.0;

    // Вычисляем расстояние от текущей позиции до центра экрана
    float distanceToCenter = length(fragCoord - center);

    // Вычисляем радиус круга (можно настроить)
    float radius = resolution.x * 0.5;

    // Коэффициенты для квадратичной функции
    float a = 1.0 / (radius * radius);
    float b = -2.0 * a * radius;
    float c = a * radius * radius;

    // Прозрачность для желтого пятна
    float alphaYellow = a * distanceToCenter * distanceToCenter + b * distanceToCenter + c;
    alphaYellow = clamp(alphaYellow, 0.0, 1.0); // Убеждаемся, что alpha находится в диапазоне [0, 1]

    // Устанавливаем цвета для градиента
    vec3 yellowColor = vec3(1.0, 1.0, 0.0); // Желтый
    vec3 transparentColor = vec3(1, 1, 1); // Белый

    // Смешиваем цвета с учетом прозрачности для желтого пятна
    vec3 finalYellowColor = mix(transparentColor, yellowColor, alphaYellow);

    // Выводим цвет
    fragColor = vec4(finalYellowColor, 1.0);
}
