void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.y/iResolution.x;
	vec2 uv = fragCoord.xy / iResolution.x;
    uv -= vec2(0.5, 0.5*aspect);
    float rot = radians(45.0); // radians(45.0*sin(iTime));
    mat2 m = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
   	uv  = m * uv;
    uv += vec2(0.5, 0.5*aspect);
    uv.y+=0.5*(1.0-aspect);
    vec2 pos = 10.0*uv;
    vec2 rep = fract(pos);
    float dist = 2.0*min(min(rep.x, 1.0-rep.x), min(rep.y, 1.0-rep.y));  
    float edge = sin(iTime*0.1)*0.25;
    edge = 2.0*fract(edge*0.5);
    float value = fract (dist);
    //value = mix(value, 1.0-value, step(1.0, edge));
    edge = pow(abs(1.0-edge), 2.0);
    value = smoothstep( edge-0.05, edge, 0.95*value);
    value += .1;

    vec2 pos2 = 10.0*uv;
    vec2 rep2 = fract(pos2+vec2(0.5,0.5));
    float dist2 = 2.0*min(min(rep2.x, 1.0-rep2.x), min(rep2.y, 1.0-rep2.y));
    float edge2 = cos(iTime*0.1)*0.25;
    edge2 = 2.0*fract(edge2*0.5);
    float value2 = fract (dist2);
    //value2 = mix(value2, 1.0-value2, step(1.0, edge2));
    edge2 = pow(abs(1.0-edge2), 2.0);
    value2 = smoothstep( edge2-0.05, edge2, 0.95*value2);
    value2 += .1;

    fragColor = mix(vec4(1.0,1.0,1.0,1.0),vec4(0.5,0.75,1.0,1.0), value);
    fragColor.a = 0.25*clamp(value, 0.0, 1.0);

    fragColor = vec4(value+value2);
}
