void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0,uv);
}

float BLURAMOUNT = 20.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
    vec2 uv = fragCoord.xy / iResolution.xy;

    float speed = (iMouse.y/iResolution.y);
    float t = iTime*speed;

   	vec4 col0 = texture(iChannel0,uv);
    vec4 col1 = texture(iChannel1,uv);
    float col0_b = (col0.r + col0.g + col0.b)/3.0;
    float col1_b = (col1.r + col1.g + col1.b)/3.0;
    if (abs(col0_b - col1_b) > .3){
     vec4 color = vec4(mod(t*.3,.5)+(0.4*fragCoord.x/iResolution.x),
                         mod(t*.5,.5)+(0.4*fragCoord.y/iResolution.y),
                         mod(t*.9,.9), 1.0);
        fragColor = color/BLURAMOUNT + 
        (BLURAMOUNT-1.)*texture(iChannel1,uv)/BLURAMOUNT;
    }else{
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }
    //fragColor = abs(texture(iChannel0,uv)/BLURAMOUNT + 
    //(BLURAMOUNT-1.)*texture(iChannel1,uv)/BLURAMOUNT);
}
