#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))
#define L length(p)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=1.; vec3 p, r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1);
    p=vec3(log2(L),exp2(p.y)-tan(iTime*.4),atan(p.x,p.z));
    p.xy*=R2D(iTime*.3);

    for(j=0.;j++<16.;)
        p=abs(abs(p)-.05),
        d=max(max(p.z,p.y),p.x),
        d-=dot(cos(p/L),tan(p/L-.5));

}o+=vec4(sqrt(p),1)*9./i;
}
