// Forked from https://www.shadertoy.com/view/MsfcD8
// Added Frame and played around with the colors for the visualizer
// Was in a Rick&Morty mood, so I've changed the soundcloud song

vec4 WoodShader(vec4 col,vec2 uv, float bass){
   if(bass < 1.5) {
       return vec4((texture(iChannel3,uv).rgb+col.rgb)/2.,col.a);
   }
   return col;
}

vec4 renderAnimation(vec2 uv, float bass){
    vec2 ruv=uv;
    float s;
    int tick = int(iTime*8.);
    tick%=6;

    ruv.x/=6.;
    ruv.x+=float(tick)*(40. / 256.);

    return WoodShader(texture(iChannel1,ruv),ruv, bass); 
}

vec4 renderNaynBass(vec2 uv,float bass){
    vec2 fir = vec2(0.5) - (bass/4.) ;
    vec2 sec = vec2(0.5) + (bass/4.) ;
    if(fir.x<=uv.x)if(fir.y<=uv.y)if(uv.x<=sec.x)if(uv.y<=sec.y){
        return renderAnimation((uv-fir)*(2./bass), bass);
    }
    return vec4(0);
}

vec3 SpaceShader(vec3 color,float bass){
    float bright=color.x*color.y*color.z;
    if(bright<bass)return vec3(bass-bright*2.);
    return vec3(bright);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv2 = -1.0 + 2.0 * uv;
	vec2 sound = texture(iChannel0,vec2(0)).xy;
    float bass = (sound.x+sound.y)/2.;
    fragColor=vec4(0);
    fragColor=renderNaynBass(uv,bass*3.+0.5);
    if(fragColor.a<0.5){
        fragColor.rgb=SpaceShader(texture(iChannel2,fract(uv+vec2(iTime/2.,0.))).rgb,bass);
    }
    if(uv.y<texture(iChannel0,vec2(uv.x)).x/5.){
        fragColor.r=sin(iTime*bass);
        fragColor.g=sin(iTime)*uv.x+uv.y+bass;
        fragColor.b=cos(iTime+bass)+uv.x+sound.x;
    }
    vec3 resultColorWithBorder = mix(vec3(0.),vec3(fragColor.x, fragColor.y, fragColor.z),pow(max(0.,1.5-length(uv2*uv2*uv2*vec2(10.0-bass*18.>2.?10.0-bass*18.:2.,10.0-bass*18.>2.?10.0-bass*18.:2.))),.3));
    fragColor = vec4(resultColorWithBorder, 1.0);
}
