#define M1 1597334677U     //1719413*929
#define M2 3812015801U     //140473*2467*11
float hash_Tong(uvec2 q)
{
	q *= uvec2(M1, M2);
    uint n = q.x ^ q.y;
    n = n * (n ^ (n >> 15));
    return float(n) * (1.0/float(0xffffffffU));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = 0.3 * iTime;
    vec2 uv = 2.0 * fragCoord/iResolution.xy;
    uvec2 up = uvec2(fragCoord)*uint(iFrame);
    float noise = hash_Tong(up);
    vec3 col = (0.95 + 0.05 * sin(cos(0.2 * uv.y + t) + uv.x * uv.y + 0.893 * t)) * 0.5 + 0.5*cos(t+2.0*uv.xyx+vec3(0,2,4));
    vec2 frac = fract(2.0 * uv + 0.0 * sin(uv.x * uv.y + t));  
    frac = tanh(4.0 * frac * (1.0 - frac));
    float spray = 0.0 * 0.5 * pow(noise, 8.0);
    //float spray = 0.15 * pow(noise, 1.0);
    vec3 base = vec3(1.5 + spray);
    vec3 color = max((12.0 * sqrt((frac.x * 0.05) * frac.y)) * col - spray, vec3(0.0));
    //color = base - color;
    color = pow(color, vec3(3.0));
    color = floor(4.0 * color) / 4.0;
    color = 1.25 * tanh(color);
    color = 1.25 * tanh(color);
    color = 1.25 * tanh(color);
    //color = 1.0 - color;
    fragColor = vec4(color,1.0);
}
