float sampl(vec2 uv, float time)
{
     vec2 s = smoothstep(0.1,0.2,sin(uv*60.+vec2(iTime,0.))*.5+.5);
     return s.x*s.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;

    // Time varying pixel color
    vec3 col = vec3(sampl(uv,iTime));
    if(abs(uv.y-(sin(2.2*iTime)*.5+.5)*iResolution.y/iResolution.x)<0.1)
    {
        col = vec3(0.);
        for(float dx = -5.;dx<5.1;dx += 1.)
        for(float dy = -5.;dy<5.1;dy += 1.)
            col+=sampl(uv+vec2(dx,dy)/333.,iTime);
        col /= 121.;
   }

    // Output to screen
    fragColor = vec4(col,1.0);
}
