#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)
#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(0,1.4,-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1)*64.;
    p.yz*=R2D(iTime*.05);

    for(j=0.;j++<3.;)
        p=abs(abs(R3D(atan(iTime*.002)))),
        d=cos(tan(p.x-p.z))*1e4,
        d-=dot(cos(p/2.5),sin(q/4.5));

    e+=d;

}o+=3./i;
}
