#define EPSILON 0.001
#define DIST_MAX 50.0
#define ITER_MAX 200u

#define pi 3.1415926

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

float box(vec3 p, vec3 b) // https://iquilezles.org/articles/distfunctions/
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec4 combine(vec4 a, vec4 b)
{
    return a.w < b.w ? a : b;
}

float frame(vec3 p, vec3 b, float e) // https://iquilezles.org/articles/distfunctions/
{
    p = abs(p)-b;
    vec3 q = abs(p+e)-e;
    return min(min(
        length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
        length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
        length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float diamond(vec2 p)
{
    p = abs(p);
    float h = clamp(p.y-p.x, -1.0, 1.0);
    float d = length(p-0.5*vec2(1.0-h,1.0+h));
    return d * sign(p.x + p.y - 1.0);
}

float diamond_prism(vec3 p, float s, float h)
{ // extrusion from https://iquilezles.org/articles/distfunctions/
    float d = diamond(p.xy/s)*s;
    vec2 w = vec2(d, abs(p.z) - h);
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float hexagon(vec2 p, float r) // adapted from https://iquilezles.org/articles/distfunctions2d/
{
    const vec3 k = sqrt(vec3(3.0/4.0,1.0/4.0,1.0/3.0)) * vec3(-1,+1,+1);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

float hexagon_prism(vec3 p, float s, float h)
{ // extrusion from https://iquilezles.org/articles/distfunctions/
    float d = hexagon(p.yx, s);
    vec2 w = vec2(d, abs(p.z) - h);
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

vec4 map(vec3 p)
{
    // coordinate-system along plane orthogonal to diagonal (1,1,1)
    mat3 T =
        mat3
        (
            vec3(+1,-1, 0)/sqrt(2.0),
            vec3(-1,-1, 2)/sqrt(6.0),
            vec3(-1,-1,-1)/sqrt(3.0)
        );

    return
        combine
        (
            combine
            (
                vec4(vec3(1,0,0), diamond_prism(p-vec3(-0.5,0.5,0.0),0.5,1.0)),
                combine
                (
                    vec4(vec3(0,1,0), diamond_prism(p.xzy-vec3(0.5,-0.5,0),0.5,1.0)),
                    vec4(vec3(0,0,1), diamond_prism(p.zyx-vec3(0.5,-0.5,0),0.5,1.0))
                )
            ),
            combine
            (
                vec4(vec3(1,1,0), hexagon_prism(p*T, sqrt(2.0)/4.0/*guessed*/, 1.0)),
                vec4(vec3(1,1,1), frame(p, vec3(1.0), 0.02))
            )
        );
}

// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return
        normalize
        (
            k.xyy * map(p + k.xyy*h).w
            +
            k.yyx * map(p + k.yyx*h).w
            +
            k.yxy * map(p + k.yxy*h).w
            +
            k.xxx * map(p + k.xxx*h).w
        );
}

vec4 trace(vec3 ro, vec3 rd, float t0, float t1) // pass on running out of iterations
{
    // NOTE: Limited number of iterations to avoid stalling
    //       when ray passes closely (just above EPSILON)
    //       in parallel to a surface.
    uint i;
    vec4 h = vec4(0);
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++)
    {
        h = map(ro + rd * t);
        if(h.w < EPSILON)
            return vec4(h.xyz,t);
        t += h.w;
    }

    return vec4(h.xyz,t); // stop on running out of iterations
    //return vec4(0,0,0,t1); // pass on running out of iterations
}

// NOTE: Don't forget to add +normal*EPSILON to the starting position
//       to avoid artifacts caused by getting stuck in the surface
//       due to starting at distance < EPSILON from the surface.
//       (normal could be calculated here but that would most likely be redundant)
float shadow(vec3 ro, vec3 rd, float t0, float t1)
{
    return trace(ro, rd, t0, t1).w < t1 ? 0.0 : 1.0;
}

// https://iquilezles.org/articles/rmshadows
float softshadow(vec3 ro, in vec3 rd, float t0, float t1, float k)
{
    float res = 1.0;
    float ph = 1e20;
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++)
    {
        float h = map(ro + rd*t).w;
        if( h < EPSILON )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}

// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).w;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

void mainImage(out vec4 o, vec2 I)
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    I = (2.0 * I - R) / R.y; // concise scaling thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    float pitch = pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : pi / 3.0 + 2.0 * pi * fract(iTime/100.0);
    //pitch = !demo ? pitch : 4.0/3.0 * pi / 2.0;
    pitch = !demo ? pitch : 2.8/2.0 * pi / 2.0;
    //pitch = !demo ? pitch : atan(sqrt(2.0/3.0)) + pi/2.0;
    //pitch = !demo ? pitch : (0.5-0.5*cos(iTime/pi/10.0)+3.0)/3.0 * pi / 2.0;

    vec3 ro = vec3(0.0, 0.0,-5.0);
    vec3 rd = normalize(vec3(I.xy, 2.5)); // NOTE: omitting normalization results in clipped edges artifact

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ro = M * ro;
    rd = M * rd;
    //ro.z += 0.0;

    //vec3 lightdir = normalize(vec3(1.0, 1.0, 1.0));
    //vec3 lightdir = normalize(vec3(-1.0,-2.0, 3.0));
    vec3 lightdir = normalize(vec3(-2.0,-1.0, 3.0));

    vec4 result = trace(ro, rd, 0.0, DIST_MAX);
    vec3 color = result.xyz;
    float dist = result.w;
    if(dist < DIST_MAX)
    {
        vec3 dst = ro + rd * dist;
        vec3 n = normal(dst);

        //color = n * 0.5 + 0.5;

        vec3 ambient = vec3(0.1);
        float brightness = max(dot(lightdir, n), 0.0) * 2.0;
        if(brightness > 0.0)
            brightness *= shadow(ro + rd * dist + n * 0.01, lightdir, 0.0, DIST_MAX);
            //brightness *= softshadow(ro + rd * dist + n * 0.01, lightdir, 0.0, DIST_MAX, 20.0);
        //color *= (ambient * calcAO(dst, n) + brightness);
        color *= (ambient * mix(0.5,1.0,calcAO(dst, n)) + brightness); // tone down AO a bit

        if(brightness > 0.0)
        {
            float specular = pow(max(0.0, dot(n, normalize(-rd + lightdir))), 250.0);
            color += specular;
        }

        vec3 fog_color = vec3(0);
        color = mix(fog_color, vec3(color), exp(-pow(dist/20.0, 2.0))); // fog
    }
    else
    {
        color *= 0.0;
    }

    color = tanh(color); // roll-off overly bright colors
    color = sqrt(color); // approximate gamma

    //color += hash32(I*123.0)/255.0; // dither to remove banding in the background

    o = vec4(color, 1);
}
