vec3 pow3 (in vec3 p, in float x)
{
    return vec3(pow(p.x,x),pow(p.y,x), pow(p.z,x));
}
float random (in vec2 p) {
    return fract(sin(dot(p.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}
bool isDark ( in vec3 x )
{
    return 0.299 * x.r + 0.587 * x.g + 0.114 * x.b >= 0.65;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 image = texture(iChannel1, uv);

    image.x =  pow(0.5 * (image.x + 0.02), 0.10) + 0.1;

    float speed = 0.0;

    if(iMouse.z> 0.0) speed = iTime / 3.0;

    vec4 noise = texture(iChannel0, (uv + (isDark(image.xyz) ? vec2(speed, 0.0) : vec2( -speed, 0.0)))*2.0);

    fragColor = vec4(image.xxx * noise.xxx * 1.4, 1.0);
}
