#ifdef GL_ES
precision mediump float;
#endif

#define PI 3.14159265358979323846

float hash (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = gl_FragCoord.xy/iResolution.xy;
    float tile = 15.;
    float lineWidth = 0.1;
    st *= tile;
    st += iTime*1.5;
    vec2 gv = fract(st);
    vec2 f1 = floor(st);
    float random = hash(f1);
    float col = smoothstep(gv.x,gv.x+0.002,gv.y) - 
    smoothstep(gv.x+lineWidth,gv.x+0.002+lineWidth,gv.y);

    float col2 = smoothstep(gv.x,gv.x+0.002,1.-gv.y) - 	
    smoothstep(gv.x+lineWidth,gv.x+0.002+lineWidth,1.-gv.y);

    float finalColor = col;
    if(random >= 0.5)finalColor = col2;
    fragColor = vec4(vec3(finalColor,.84,.7),1.0);
}
