#define F for (float i; i++ < 9.;)

void mainImage( out vec4 o, vec2 u )
{
    vec2 p = iResolution.xy,
         c = vec2(2,4) * ceil(.6*iTime) + 150.;     

    u = (u+u-p)/p.y;
    u /= 1.+.01*dot(u,u);
    u += fract(.6*iTime)/9.;
    o = vec4(1);

    F {
        u *= mat2(cos(c.x+vec4(0,11,33,0)));
        p = u - 8.*cos(i-c);
        F
            p += p,
            o += .005
               + .07*(texture(iChannel0, (p+c)/2e4)-.5)
               + cos(o+length(p)-length(cos(p)))/(6.+i);       
    }

    o = 1.-o/4./max(1., abs(u.y));
}
