float Xor(float a, float b)
{
  return a*(1.-b) + b*(1.-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.xy;

    vec3 color = vec3(0);

    float a =.78;
    float s = sin(a);
    float c = cos(a);
    uv *= mat2(c, -s, s, c);
    uv *= 15.;
    vec2 gv =  fract(uv * 5. ) - .5;
    vec2 id = floor(uv*5.);


    float d  = length(gv);
    float m = 0.;
    float t = iTime * 2.;
    //float dist = length(id)*30.;
    for(float y=-1.; y <= 1.; y++)
    {
      for(float x=-1.; x<=1.; x++)
      {
        vec2 offs = vec2(x, y);
        float d = length(gv - offs);
        float dist = length(id+offs)*.3;
        float r = mix(.3,1.5,sin(dist-t)* .5+.5);
        m += smoothstep(r,r*.5, d) * .3;
      }
    }

    //color.rg = gv;
    color += mod(m, 2.);

    fragColor = vec4(color,1.0);
}
