// code ref:https://www.shadertoy.com/view/l3jXDV

#define PI 3.14159265359

float plot(vec2 st, float pct){
  return  smoothstep( pct-0.15, pct, st.y) -
          smoothstep( pct, pct+0.15, st.y);
}

float easeOutHalfQuart(float x) {
	return 1.0 - pow(1.0 - x, 2.0);
}

float halfQuad(float x) {
    return pow(cos(x * PI / 2.0), 3.0);
}

float easeInSine(float x) {
	return 1.0 - 0.8* cos((x * PI) / 1.5);
}
float slowchangecos(float time){
    return 1.0 - cos((time * PI) / 5.0);
}
vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - rad;
	float t = clamp(d, 0.0, 1.0);
	return vec4(color, 1.0 - t);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 colorA = vec3(0.806,0.045,0.812);
    vec3 colorB = vec3(0.800,0.528,0.524);
    vec3 colorC = vec3(0.507,0.515,0.800);
    vec3 colorSun = vec3(0.985,0.178,0.444 * slowchangecos(iTime));

    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);
    vec3 pct = vec3(st.y);
	vec2 uv = gl_FragCoord.xy;
	vec2 center = vec2(iResolution.x * 0.8, iResolution.y * 0.45);
	float radius = 0.04 * iResolution.y;

    pct.r = easeOutHalfQuart(0.5-st.x) * smoothstep(0.8, 1.0, st.y);
    pct.b = easeInSine(st.y);
    pct.g = 0.0;
    color = mix(colorB, colorA, pct * abs(sin(iTime/PI)));

    pct = vec3(halfQuad(st.y + st.x * 2.0)) * smoothstep(0.0, 1.5, st.y);
    color = mix(color, colorA, pct);

    pct = vec3(halfQuad(st.y*2.0 + st.x)) * smoothstep(0., 0.8, st.y) * smoothstep(1.5, 0.0, st.x);
    color = mix(color, colorC, pct * slowchangecos(iTime));

    pct = vec3(plot(st, 0.25) * 0.3);
    color = mix(color, colorC , pct * slowchangecos(iTime));

    vec4 layer1 = vec4(color, 1.0);
    vec4 layer2 = circle(uv, center, radius, colorSun);

    fragColor = mix(layer1, layer2, layer2.a * 0.5 * cos(iTime* PI / 6.0));

}
