// 引用自：https://www.shadertoy.com/view/7sdXz2，作者：iq，遵循MIT许可证
float sdRoundSquare( in vec2 p, in float s, in float r ) 
{
    vec2 q = abs(p)-s+r;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    // Time varying pixel color
    float d = sdRoundSquare( uv, 0.4, iTime*0.3 );

    // Magic
    vec3 col = (d>0.0) ? vec3(0.9,0.4,0.3) : vec3(0.75,0.85,1.0);
    col = 0.5 + 0.5*cos(iTime+col);
    col *= 1.3 + 0.8*cos((iTime*d)*1.2);

    // Output to screen
    fragColor = vec4(col,1.0);
}
