#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//Rodrigues-Euler axis angle rotation
#define ROT(p,axis,t) mix(axis*dot(p,axis),p,cos(t))+sin(t)*cross(p,axis)

//formula for creating colors;
#define H(h)  (  cos(  h + vec3(1,2,3)   )*.7 + .4 )


//formula for mapping scale factor 
#define M(c)  log(c)

#define R iResolution

#define ex exp(w.x)
#define cy cos(w.y)
#define sy sin(w.y)
#define cz cos(w.z)
#define sz sin(w.z)
#define cw cos(w.w)
#define sw sin(w.w)

float params[] =  float[](.05,.15,.25,.5);

void mainImage( out vec4 O, vec2 U) {

    int param = int(mod(iTime/2.,4.));

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .7*R.y, R.y))*400.;

    float sc,dotp,totdist=0., tt=iTime, t=0.;

    for (float i=0.; i<60.; i++) {

        vec4 p = vec4( rd*totdist);

        p.z -= .1; 

        p.xz *= rot( tt/3.  + abs(sin(tt/6.)) );

        p.yzw = sin(p.xyz*3.);

        sc = .6; 

        p.zw *=  rot( tt/2. );
        vec4 w = p;

        for (float j=0.; j<5.; j++) {

            p = abs(p)*.5;

            dotp = max(1./dot(w,w),.2);

            sc *= dotp; 

            p = p * dotp - .4; 

            w = p;

            for (float k=0.; k<2.; k++) {
            //this is exp(p.x+i*p.y+j*p.z+k*p.w) where ijk are the quaternion group
                 w =
                    params[param]*
                    (vec4( 
                        ex*(cy*cz*cw - sy*sz*sw),
                        ex*(sy*cz*cw + cy*sz*sw),
                        ex*(cy*sz*cw - sy*cz*sw),
                        ex*(sy*sz*cw + cy*cz*sw) ) - vec4(1,0,0,-1) ); 

            }

        }

        float dist = abs( length(p.zw )-.01)/sc ;  //funky distance estimate
        float stepsize = dist/200. ;     
        totdist += stepsize;                      //move the distance along rd

        //accumulate color, fading with distance and iteration count
        c +=
             .25e-1* 
             mix( vec3(1), H(M(sc)),.9) * exp(-i*i*stepsize);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
