#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,s=.2; vec3 p,q=vec3(-2,0,-2),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*e;
    p.yz*=R2D(iTime*.5);

    for(j=0.;j++<36.;)
        p=abs(abs(p*e-floor(p))-.04)-.015,
        d=max(min(p.z,p.y),p.x),
        d-=dot(p,p);

    e-=d;

}o+=vec4(exp(p),1)*8./i;
}
