void mainImage( out vec4 U, in vec2 V )
{
    V = (V+V-iResolution.xy) / iResolution.y;
    float q = atan(V.y, V.x) * 9. 
      - length(V)*(sin(iTime)*4.)*4.
      + sin(iTime)*30.;
    U = sqrt(.5+.5*sin( vec4(0,21,23,0)+q));
}

#define PI (3.14159265)
#define TAU (PI*2.)
vec2 mainSound( int samp, float time )
{
    float k = 2.*time+PI/8.;
    float vol = .1 * sin(time+PI/2.);
    return vec2(
        vol*sin(60.*sin(TAU*time*30.-10.*(sin(k))))
    );
}
