#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//Rodrigues-Euler axis angle rotation
#define ROT(p,axis,t) mix(axis*dot(p,axis),p,cos(t))+sin(t)*cross(p,axis)

//formula for creating colors;
#define H(h)  (  cos(  h*4.*(1.+.8*sin(iTime/2.+sin(iTime/4.))) + vec3(1,2,4)   )*.6 + .5 )

#define H2(h)  (  cos(  h*5. + vec3(3,2,1)   )*.7 + .2 )

//formula for mapping scale factor 
#define M(c)  log(1.+c)

#define R iResolution


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .7*R.y, R.y))*300.;

    float sc,dotp,totdist=0., tt=iTime/3., t=0.;

    for (float i=0.; i<60.; i++) {

        vec4 p = vec4( rd*totdist);

        p.xyz += vec3(.5,0,-2.3); 

        p.xz *= rot( .8   );

        p.yzw = p.xyz;

        sc = 1.; 

        p.zw *= rot( .7 + tt/2. );

        p.xw *= rot( tt);

        vec4 w = p;

        for (float j=0.; j<7.; j++) {

            p = abs(p);
            p = sqrt ( p.xyzw * p.yzwx ) *.78;

            dotp = max(1./dot(w,w),.1);
            sc *= dotp; 

            p = p * dotp - .51; 

            w = log(1.+log(1.+log(1.+p)*p)*p) - .25; 


        }

        float dist = length(p)/sc ;

        float stepsize = dist/30. ;     
        totdist += stepsize;                  //move the distance along rd

        //accumulate color, fading with distance and iteration count
        c +=
             6e-2* ( H2(  atan( p.w, p.z ))
             + mix( vec3(1), H(M(sc)),.95) ) * exp(-i*i*stepsize*stepsize*5e6);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
