// check common for the actual sequence, this is just rendering a general graph
const float thickness = 0.;
const float falloff = 4.0;
const uint steps = 512u;
void mainImage( out vec4 O, in vec2 I )
{
    float scale = iTime;
    vec2 uv = scale * (2.*I-iResolution.xy)/iResolution.y;
    float val = pow(abs(uv.y - f(uv.x, steps)) - thickness + 1., -falloff);
    vec3 col = vec3(val);
    O = vec4(col,1.0);
}

// the good stuff
float f(float x, uint steps)
{
    for(uint i; i < steps; i++)
    {
        if(abs(mod(x, 2.) - 1.) > 0.5)
        {
            x *= 0.5;
        }
        else
        {
            x = x * 3. + 1.;
        }
    }
    return x;
}
