void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv01 = fragCoord / iResolution.xy;

    float uvtan = tan(iTime) * 0.5 + 0.5;
    float d = length(uv);
    d -= 0.75 * uvtan * 0.5 + 0.5;
    d = abs(d);
    d = step(0.75, d);

    float gradientModulation = 1.0 - d;

    vec3 bgColor = vec3(1.0, uv01.yx); 
    vec3 ringColor = vec3(uv01.xy, 1.0) * gradientModulation; 
    vec3 color = mix(bgColor, ringColor, gradientModulation);

    fragColor = vec4(color, 1.0);
}
