float rand(vec2);
vec2 offset = vec2(0., 0.);
vec2 cell = vec2(0., 0.);
float cellsize = 1.;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float scale = cellsize;
    cell = floor(fragCoord / scale) * scale;

    highp float x = rand(cell.xy+vec2(0.5, 0.5));
    highp float y = rand(cell.xy);
    highp float z = rand(cell.xy+vec2(-.5, -.5))* abs(sin(cell.x));

    highp float r = x * z;
    highp float g = (x + y) * 0.1;
    highp float b = x * z * 0.2;

    fragColor = vec4(b, g, b, 1.0)*1.5;
}

float rand(vec2 co) {
    cell = floor(co / 1.) * 1.;
    return fract(sin(cell.x) * -cos(cell.y));
}
