#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(t)(cos((vec3(0,2,-2)/3.+t)*6.24)*.5+.5)
#define n1Q(p) p *= 2.*ri(round(atan(p.x, p.y) * 4.) / 4.)
#define ri(a) mat2(cos(a + asin(vec4(0,1,-1,0))))
#define D(a)length(vec2(fract(log(length(a.xy))+iTime*.5)-.5,a.z))/3.-.005*pow(l,.03)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution;

    for(float g,e,i=0.,l;
        ++i<99.;
        e<.005?O.xyz+=mix(vec3(1),H(l),.7)/i:p
        )
    {


        p=R(g*normalize(vec3((C-.5*r.xy)/r.y,1.))-vec3(0,0,6),
            normalize(vec3(0,1,1)),
            iTime*1.0
        );

        l=length(p);
     n1Q(p.yx);
     p.x+=2.01*cos(iTime);
      p.z*=2.01*sin(iTime);
           n1Q(p.xz);
        g+=e=(max(D(p.xzz),D(p.zyx)),D(p.zyx));    
    }
}
