float BLURAMOUNT = 60.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixel = 1.0 / iResolution.xy;
    vec2 uv = fragCoord / iResolution.xy;

    vec4 fast = texture(iChannel0, uv);
    vec4 slow = (texture(iChannel1, uv) * -1.0) + 1.0;
    vec4 motion = mix(fast, slow, 0.5);

    fragColor = pow(motion, vec4(2)) * 2.0;
    float bright = (fragColor.r + fragColor.g + fragColor.b) / 3.0;
    float hyst = .08;
    if ((bright > (0.5-hyst)) && (bright < (0.5 + hyst))){
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }else{
        fragColor = fast/BLURAMOUNT + 
                    (BLURAMOUNT-1.)*slow/BLURAMOUNT;
    }
}

vec4 factor = vec4(0.4, 0.6, 0.8, 0.2);

vec2 diskPt(vec2 p) {
    return sqrt(p.x) * vec2(sin(6.2832 * p.y), cos(6.2832 * p.y));
}

vec2 hash21(float p) {
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec4 prv = texture(iChannel1, uv);
    vec4 cam = texture(iChannel0, uv);
    fragColor = mix(prv, cam, factor);
}
