float noise(in vec2 uv){
    return fract(sin(uv.x*100.0+uv.y * 6579.)*5647.0);
}



//moon
float moon(in vec2 uv)
{
    uv.y += -0.4;

    float d = length(uv);

    float s = smoothstep(.01,-.01,length(uv-vec2(.4,.2))-.2);
    s *= smoothstep(-.01,.01,length(uv-vec2(.5,.25))-.2);

    s *= 3.0;

    return s;

}



