#define R(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime
vec3 cor;

void rotObj(inout vec3 p) {
    vec2 C = 3. * cos(.3 * t + vec2(0, 11));    
    p.yz *= R(C.y);
    p.xz *= R(C.x);
}



float map(vec3 p){
        rotObj(p); 
        cor = vec3(1, 4, 8);

        float s, pi5 = acos(-1.) / 5.;
        vec2 q = p.xy;

        //if(false)
        q = vec2(
               atan(p.x, p.y),
               length(p.xy)
            );

        p.z = abs(p.z);

        q.x = mod(q.x, 2. * pi5) - pi5;

        atan(p.z, q.y) < 1. 
            ? p.xy = q.y * cos(q.x + vec2(0, 11)), 
              cor = vec3(8, 4, 1),
              p.xz *= R(-1.12), p 

            : p = p.yxz, 





        s = max(
                length(p.xy) - 1.3, 
                abs(length(p) - 3.) - .1
            ); 

        return s;
}





void mainImage(out vec4 o, vec2 u) {
    float j, l, d, s = 1., t = iTime;

    vec3 r = iResolution, p, q,
         D = vec3((u - r.xy / 2.) / r.y, 2.1);

    o = vec4(1);
    p.z -= 14.;

    while(o.x > 0. && s > .01) 
        s = map(p),
        p += .4 * s * D, 
        o -= vec4(cor, 0) / 2e2;

    o *= o * o * o;
}
