//const float PI = 3.14159265358979;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;
    float r = pow(pow(cos(iTime * 1.1)/2.0+0.5-p.x,2.0)+pow(sin(iTime * 1.1)/2.0+0.5-p.y,2.0),0.5);
    float g = pow(pow(cos(iTime * 1.2)/2.0+0.5-p.x,2.0)+pow(sin(iTime * 1.2)/2.0+0.5-p.y,2.0),0.5);
    float b = pow(pow(cos(iTime * 1.3)/2.0+0.5-p.x,2.0)+pow(sin(iTime * 1.3)-p.y,2.0)/2.0+0.5,0.5);
    vec3 col = vec3(r,g,b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
