vec3 palette(float t ){

    vec3 a =vec3(0.212,0.729,0.729);
    vec3 b =vec3(0.758,0.618,0.438);
    vec3 c =vec3(0.090,0.510,0.137);
    vec3 d =vec3(-0.012,0.5,0.667);

    return a + b*cos( 6.283185*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord /iResolution.xy;


    vec3 fianlcol = vec3(0.0);


   uv = uv-0.5;
   uv *= 2.0;

   uv.x = uv.x*(iResolution.x/iResolution.y);

 //  uv.x -=  (iResolution.x/iResolution.y) /2.0 ;
  // uv.y -= 0.5;


  //uv *=2.;
   vec2 uv0= uv;
  for(float i = 0.0;i<2.0;i++)
  {

  uv = fract(uv);
  uv -=0.5;
  uv*=1.7;

   float d = length(uv) * exp(-length(uv0)+0.2);//(0,1)
    vec3   col = palette(length(uv0)+0.3+iTime*1.3);
   d = sin(d * 2.0+ iTime*.8 );//(-1,1)

   d = abs(d);//(0,1)
   d = smoothstep(0.0,0.4,d);
   d = 0.06/d;//(0.1,+00)
   d = clamp(d,sin(iTime)*.1,1.0);

  col *=d;
  fianlcol += col*d;
  }



    fragColor = vec4(fianlcol,1.0);
}
