void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float time = iTime*0.1;

    // Background
    vec3 color = vec3(0.0, 0.05, 0.1) * (1.0 - uv.y);

    // Fungus shape and growth pattern
    for (int i = 1; i < 10; i++) {
        float fi = float(i);
        vec2 pos = vec2(sin(fi*0.3 + time)*0.3, cos(fi*0.5 + time)*0.3);
        float d = length(uv - 0.5 - pos) * 1.5;
        color += vec3(0.3, 0.5, 0.1) * exp(-d*d*10.0);
    }

    // Adding bioluminescent glow effect
    float glow = sin(time*5.0)*0.5 + 0.5;
    color *= vec3(glow, glow*0.8, glow*0.5);

    fragColor = vec4(color, 1.0);
}
