#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec2 mid = iResolution.xy / 2.0;
    float d = distance(fragCoord.xy, mid);
    float or = min(iResolution.x, iResolution.y)/5.0;
    or = or+cos(iTime)*or/10.0;

    float progress = (smoothstep(0., d*1.5, or)-smoothstep(d-1., d+1., or));

    vec3 tint1 = vec3(.15, .08, .29);
    vec3 tint2 = vec3(.85, .90, .25);
    vec3 color = mix(tint1, tint2, progress);

    fragColor = vec4(color, 1.0);
}
