#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1,0,1.5),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    //p.z-=4.;

    for(j=0.;j++<6.;)
        p=abs(abs(p-R3D(iTime)*mix(q,p,1.64))),
        d=min(max(p.z,p.y),p.x),

    e-=d;
}o+=vec4(exp(p-q),1)*3./i;
}
