void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.*fragCoord-iResolution.xy)/((iResolution.x-400.)),z =vec2(0.);
    float iter = 0., maxiter=50.;
    while (length(z)<2. && iter < maxiter){
        z = vec2(z.x*z.x + z.y*z.y, 0.4*iTime*z.x*z.y)+p;
        iter += 1.;
    }
    fragColor = vec4(iter/maxiter);
}
