// Referencing https://www.shadertoy.com/view/ttB3Rh


vec3 colorA = vec3(0.149,0.141,0.912);
vec3 colorB = vec3(0.800,0.833,0.224);
vec3 colorC = vec3(0.5,0.5,0.5);

vec3 colorA_B;
vec3 colorB_C;

float sdCircle( in vec2 p, in float r)
{
    return length(p) - r;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float h = 1.0*abs(sin(iTime*0.5));

    // Time varying pixel color
    vec3 pct = vec3(uv.y);
    colorA_B = mix(colorA, colorB, pct.y/h);
    colorB_C = mix(colorB, colorC, (pct.y - h)/(1.0-h));

    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;


    vec3 col_circle = vec3(step(0.0, sdCircle(uv, 0.25)));


    vec3 col = mix(colorA_B, colorB_C, step(h,pct));
    col -= (col_circle*(sin(iTime*3.0))*0.2);

    // Output to screen
    fragColor = vec4(col,1.0);
}
