float rect(in vec2 st, in vec2 size) {
	size = 0.25-size*0.25;
    vec2 uv = step(size,st*(1.0-st));
	return uv.x*uv.y; }

float Line(vec2 p, vec2 a, vec2 b) {
	vec2 pa = p - a, ba = b - a;
    float t = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);
    vec2 c = a+ba*t;
    float d = length(c-p);
    float l = 0.002, g = 0.002;
	return min(1.0, 0.003/d); }

