    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 uvf = fract(uv*25.0);
    vec3 col = vec3(0.25, 0.55, 1.0)*0.2;
    col += Line(uvf, vec2(0.0, 0.0), vec2(1.0, 0.0))*0.2;
    col += Line(uvf, vec2(0.0, 1.0), vec2(0.0, -1.0))*0.2;
