float sdRoundSquare( in vec2 p, in float s, in float r ) 
{
    vec2 q = abs(p)-s+r;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    p.x += 0.5*cos(0.5*iTime);
    p.y += 0.5*sin(0.5*iTime);

	float si = 0.25*sin(1.0*iTime+5.0)+0.25;

    float ra = 0.1*cos(1.0*iTime)+0.1;

	float d = sdRoundSquare( p, si, ra );

    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.5) : vec3(0.9,0.1,0.1);
    col = (sin(0.5*iTime)>0.0)&&(d<0.0) ? vec3(0.45,0.48,0.52) :col;
	col *= 1.0 - exp(-4.0*abs(d))*sin(0.5*(iTime));
	col *= 0.6 + abs(0.4*cos(150.0*d)*cos(0.25*iTime));
	col = mix( col, vec3(1.0)*step(sin(0.5*iTime),0.0), (1.0-smoothstep(0.0,0.01,abs(d))));

	fragColor = vec4(col,1.0);
}
