// distance field from iq https://www.shadertoy.com/view/Xds3zN
float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}

float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdOctahedron(vec3 p, float s)
{
    p = abs(p);
    float m = p.x + p.y + p.z - s;

    // exact distance
    #if 0
    vec3 o = min(3.0*p - m, 0.0);
    o = max(6.0*p - m*2.0 - o*3.0 + (o.x+o.y+o.z), 0.0);
    return length(p - s*o/(o.x+o.y+o.z));
    #endif

    // exact distance
    #if 1
 	vec3 q;
         if( 3.0*p.x < m ) q = p.xyz;
    else if( 3.0*p.y < m ) q = p.yzx;
    else if( 3.0*p.z < m ) q = p.zxy;
    else return m*0.57735027;
    float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
    return length(vec3(q.x,q.y-s+k,q.z-k)); 
    #endif
}

#define R(a) mat2(cos(a),sin(a),-sin(a),cos(a))

void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    float color = 0.0;
    vec3 col;

    // screen to world
    vec3 rd = vec3((fragCoord - 0.5 * iResolution.xy) / iResolution.y, 1);
    rd = normalize(rd);
    rd.xz *= R(iTime * 0.2);
    rd.yz *= R(iTime * 0.3);
    vec3 ro = vec3(0., 0., 1.*cos(iTime*.2));
    float l = 0.0;

    for(float i = 1.0; i < 50.0; i++)
    {
        vec3 p = ro+rd * l;
        p = mod(p, 0.2);
        p -= 0.1;
        float d = sdSphere(p, 0.05);
        //d = sdOctahedron(p, 0.05);
        //d = sdBox(p, vec3(0.025));
        l += clamp(d, 0.0, 0.2);

        if(d < 0.00001) {
            color += 1.0/i;
            //color = 1./l/l/25.; break;
        }
    }
    color = pow(color, 1./2.2);
    fragColor = vec4(vec3(color), 1.);
    //fragColor = vec4(col, 1.);
}
