const float pi = 3.141592654;
const vec4 white = vec4(0.8);
const vec4 darkBlue = vec4(0.0, 0.0, 0.2, 1.0); 
const float SPEED = 0.3;        
const float RETICULATION = 2.0; 
const float STRETCH = 2.0;
const float SPIN_CYCLE = 4.0;
const float TIME_SCALE = 0.15;   
const float START_FADE = 0.3;
const float END_FADE = 0.8;

float tex(vec2 uv) 
{
    float n = texture(iChannel0, uv, 0.0).r;
    return n;
}


float noise(vec2 uv, float t)
{
    float v = 0.0;
    float a = SPEED * 1.5 * t, co = cos(a), si = sin(a); 
    mat2 M = mat2(co, -si, si, co);
    const int L = 7;
    float s = 1.0;
    for (int i = 0; i < L; i++)
    {
        uv = M * uv;
        float b = tex(uv * s);
        v += 1.0 / s * pow(b, RETICULATION); 
        s *= 2.0;
    }

    return v / 2.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.x;
    float l = length(uv);
    float angle = atan(uv.x, uv.y) * STRETCH;
    angle += iTime * TIME_SCALE;
    angle += log(l) * SPIN_CYCLE;
    float g = fract(cos(mod(angle * 2.0, pi)));
    g *= smoothstep(0.0,START_FADE, l) - smoothstep(START_FADE,END_FADE,l);  

    float n = noise(uv, iTime * TIME_SCALE);
    float y = 0.5;
    y *= smoothstep(0.2,0.8,l);
    g *= n;
    vec4 finalColor = darkBlue + g * (white + darkBlue);
    fragColor = finalColor;
    //fragColor = vec4(y);
}
