#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//Rodrigues-Euler axis angle rotation
#define ROT(p,axis,t) mix(axis*dot(p,axis),p,cos(t))+sin(t)*cross(p,axis)

//formula for creating colors;
#define H(h)  (  cos(  h+ vec3(0,1,5)   )*.8 + .5 )

#define H2(h)  (  cos(  h*h * vec3(.01)   )*.7 + .5 )

//formula for mapping scale factor 
#define M(c)  log(c)

#define R iResolution


#define ex exp(p.x)
#define cy cos(p.y)
#define sy sin(p.y)
#define cz cos(p.z)
#define sz sin(p.z)
#define cw cos(p.w)
#define sw sin(p.w)

void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .7*R.y, R.y))*300.;

    float sc,dotp,totdist=0., tt=iTime, t=0.;

    for (float i=0.; i<60.; i++) {

        vec4 p = vec4( rd*totdist);

        p.z += -.1 ;

        p.xz *= rot( tt/2.  + abs(sin(tt/4.)) );

        p.yzw = sin(p.xyz*5.);

        sc = 1.; 

        vec4 w = p;

        for (float j=0.; j<7.; j++) {

            p = abs(p)*.75;

            dotp = max(1./dot(w,w),.1);

            sc *= dotp; 

            p = p * dotp - .25; 

            //this is exp(p.x+i*p.y+j*p.z+k*p.w) where ijk are the quaternion group
            w =
                    .2*
                    (vec4( 
                        ex*(cy*cz*cw - sy*sz*sw),
                        ex*(sy*cz*cw + cy*sz*sw),
                        ex*(cy*sz*cw - sy*cz*sw),
                        ex*(sy*sz*cw + cy*cz*sw) ) ); 



        }

        float dist = abs( length(p.zw )-.1)/sc ;  //funky distance estimate
        float stepsize = dist/200. ;     
        totdist += stepsize;                  //move the distance along rd

        //accumulate color, fading with distance and iteration count
        c +=
             .1e-1* ( H2(  atan( p.w, p.z )) 
             + mix( vec3(1), H(M(sc)),.9) )* exp(-i*i*stepsize*1e1);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
