#define MOUSE_FOLLOW true

vec2 moveToMouse(vec2 uv) {
    if (MOUSE_FOLLOW) {
        // translate mouse from screen to normalized coords
        vec2 mouse = (2. * iMouse.xy - iResolution.xy) / iResolution.y;

        // translate to mouse pos
        uv -= mouse;
    }

    return uv;
}

vec3 grid(vec2 uv) {
    vec3 backgroundCol = vec3(1.);
    vec3 gridCol = vec3(.4);
    vec3 axisCol = vec3(1., 0., 0.);

    vec3 col = vec3(backgroundCol);

    // grid
    float tick = .1;
    if (mod(uv.x, tick) <= 0.006) col = gridCol;
    if (mod(uv.y, tick) <= 0.006) col = gridCol;

    // axis
    if (abs(uv.x) <= 0.006) col = axisCol;
    if (abs(uv.y) <= 0.008) col = axisCol;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = (2. * fragCoord.xy - iResolution.xy) / iResolution.y;
    uv = moveToMouse(uv);

    vec3 col = grid(uv);

    float d = smoothstep(.505, .51, length(uv - vec2(.1, .4)));
    col = mix(vec3(0., 0., 1.), col, d);

    fragColor = vec4(col,1.0);
}
