float Xor(float a, float b)
{
    return a*(1.-b)+b*(1.-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 oUV = uv;

    // Time varying pixel color
    vec3 col = vec3(0.);

    float t = iTime;

    float a = 3.1415*2.*fract((sin(t*.03)*.5+.5));
    float c = cos(a);
    float s = sin(a);
    uv *= mat2(c, -s, s, c);

    float scale = 15.;
    uv += .5/scale; // centramos
    uv *= scale;

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    //col.xy += gv;

    float m = 0.;


    float p1 = .3+(sin(t*.00001)*.5+.5);
    float p2 = .3+(sin(t*p1)*.5+.5);
    float p3 = 1.+(sin(t*p1)*.5+.5);


    for(float y = -1.; y <= 1.; y++)
    {
        for(float x = -1.; x <= 1.; x++)
        {
            vec2 offset = vec2(x,y);

            float d = length(gv-offset);
            float dist = length(id+offset)*p1;

            float r = mix(p2, p3, sin(dist-t)*.5+.5);
            m = Xor(m, smoothstep(r,r*.95,d));
        }
    }

    col += m;

    float _R = 1. * (1.-abs(oUV.x)) * (sin(t*.567)*.45+.55);
    float _G = 1.*(1.-abs(oUV.y)) * (cos(t*.267)*.45+.55);
    float _B = 1.*(1.-abs(oUV.x+oUV.y)) * (cos(t*.167)*.45+.55);

    col *= vec3(_R,_G,_B);//vec3(mix(.1,.95,uv.x), mix(.1,.95,uv.y), mix(.1,.95,uv.x+uv.y));

    // Output to screen
    fragColor = vec4(col,1.0);
}
