struct Wave {
    vec2 pos;
    float freq;
};

Wave waves[] = Wave[](
    Wave(vec2(0.0,0.0), 0.64),
    Wave(vec2(-1.3,-0.6), -1.0),
    Wave(vec2(0.1,-0.3), 0.75),
    Wave(vec2(-0.5,0.2), -0.6),
    Wave(vec2(0.0,-0.1), 0.5),
    Wave(vec2(1.2,0.6), -0.4),
    Wave(vec2(0.2,-0.2), 1.0)
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;

    float rot = 0.2*t;
    vec2 basisX = vec2(cos(rot), sin(rot));
    vec2 basisY = vec2(-sin(rot), cos(rot));

    vec2 centre = vec2(0.5*iResolution.x/iResolution.y, 0.5);
    vec2 uv = vec2(fragCoord.x/iResolution.y, fragCoord.y/iResolution.y);
    uv -= centre;
    uv = uv.x * basisX + uv.y * basisY;

    vec3 field = vec3(0.0, 0.0, 0.0);

    float abberation = 0.5*(0.5+0.5*sin(2.0*t));

    for (int i = 0; i < waves.length(); i++) {
        Wave w = waves[i];
        field.x += sin( w.freq * ( (50.0+abberation)*distance(uv, w.pos) + 2.0*t ) );
        field.y += sin( w.freq * ( (50.0           )*distance(uv, w.pos) + 2.0*t ) );
        field.z += sin( w.freq * ( (50.0-abberation)*distance(uv, w.pos) + 2.0*t ) );
    }

    float contrast = 0.5 + 2.5*(0.5 + 0.5*sin(0.7*t));
    vec3 col = sin(field);
    //col = abs(col);
    col = vec3(0.5,0.5,0.5) + contrast * col;
    fragColor = vec4(col,1.0);
}
