#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(1.58),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p.xz*=R2D(iTime*.3);

    for(j=0.;j++<8.;)
        p=abs(p*mix(p,q,1.4))-.01,
        p*=cos(ceil(p.z)-length(p*q)),
        d-=dot(p,p)/2e4;

    e+=d/j;

}o+=vec4(sqrt(p*p*q),1)*3./i;
}
