void mainImage( out vec4 O, in vec2 U )
{
    vec3 s = iResolution;
    if (s.y > s.x) {
        U.xy = U.yx;
        s.xy = s.yx;
    }
    float r = s.y / s.x,
          l = 1. / r > 1.3 ? 4. : 3.,
          w = 2. * r / l,
          t = iTime / 4.,
          v = smoothstep(
              .4,
              .6,
              2. * abs(
                  mod(
                      t / 8. - .5,
                      1.
                  ) - .5
              )
          );
    U = abs(
        2. * U / s.x - vec2(1., r)
    );
    O.xyz = mix(
        mix(
            vec3(1,0,0),
            vec3(1,1,0),
            v
        ),
        mix(
            vec3(0,0,1),
            vec3(0,1,1),
            v
        ),
        abs(
            mod(
                l * (
                    U.x > 1. - w ?
                        U.x - 1. + w :
                        U.y > r - w ?
                            U.y - r + w :
                            U.x > 1. - 2. * w ?
                                U.x - 1.+ 2. * w :
                                U.y - r + 2. * w
                ) / r / 2. - 1. - t,
                2.
            ) - 1.
        )
    );
}
