#define h21(p) fract(sin(dot(p, vec2(12.34,26.534))) * 987.23)
#define r iResolution.xy
#define t iTime

#define lab 0

float truchet(vec2 p, bool coin, bool chess){
    p.y *= coin ? 1. : -1.;

    float d = smoothstep(.4, .6, 
                  lab == 1
                      ? abs(p.x + p.y)                      //   " / / "
                      : min(length(p + .5), length(p - .5)) //   " ) ( "
              );

    return coin ^^ chess ?  1. - d :  d;
}

void mainImage(out vec4 o, vec2 u) {
    u = 8. * (u - r / 2.) / r.y;


    vec2 q = floor(u) / 2.;
         u = fract(u) - .5;

    bool coin = h21(q) < .5,
         coin2 = h21(q + 1.) < .5,   
         chess = fract(q.y + q.x) * 2. < .5;

    o = smoothstep(40. / r.y, .0, 
            //truchet(u, coin, coin2)
            truchet(u, coin, chess)
        ) * vec4(1);

    o = mix(o, // grid
            smoothstep(30. / r.y, .0,
                abs(max(u.x, u.y) - .5))
                    * vec4(8, 4, 1, 0) / 16.,
        .5);


    if(iMouse.z > 0.)
        o = mix(o, // dots
                smoothstep(30. / r.y, .0,
                    (length(u) - .4))
                        * vec4(1, chess, 0, 0),
            .5);
}    


