void mainImage(out vec4 o, vec2 U) {
    vec2 r = iResolution.xy,
         u = (U - r / 2.) / r.y,

         h = tanh(
                 cos(
                     vec2(
                         atan(u.x, u.y) * 9.,
                         -u.x * 26.
                     ) + iTime
                 ) * 5. 
             ) * .03;

    o = o*0. + 
           -tanh(

                min(
                    max(
                        length(u) - .35 - h.x,
                        .1 - length(u)
                    ), 
                    u.y + .32 - h.y
                ) * 20.
            )
        ;
}




